package com.example.gardenflow.services;

import android.annotation.SuppressLint;
import android.content.Context;
import android.support.annotation.LayoutRes;
import android.support.annotation.NonNull;
import android.support.annotation.Nullable;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ArrayAdapter;
import android.widget.ImageView;
import android.widget.TextView;

import com.example.gardenflow.R;

import org.json.JSONException;
import org.json.JSONObject;

import java.util.ArrayList;
import java.util.List;

public class CustomAdapter extends ArrayAdapter<Plant>
{
  private Context mContext;
  private ArrayList<Plant> plantList = new ArrayList<>();

  public CustomAdapter(@NonNull Context context, @SuppressLint("SupportAnnotationUsage") @LayoutRes ArrayList<Plant> list ) {
    super(context, 0 , list);
    mContext = context;
    plantList = list;
  }

  @NonNull
  @Override
  public View getView(int position, @Nullable View convertView, @NonNull ViewGroup parent) {
    View listItem = convertView;
    if(listItem == null)
      listItem = LayoutInflater.from(mContext).inflate(R.layout.item,parent,false);

    Plant currentPlant = plantList.get(position);

    ImageView imageView = (ImageView) listItem.findViewById(R.id.plantImage);
    imageView.setImageBitmap(ImgServices.imgDecode(cutUnusedData(currentPlant.getPlantImage())));
//    imageView.setImageBitmap(ImgServices.imgDecode(""));

    TextView name = (TextView) listItem.findViewById(R.id.plantName);
    name.setText(cutUnusedData(currentPlant.getPlantName()));

    TextView species = (TextView) listItem.findViewById(R.id.plantSpecies);
    species.setText(cutUnusedData(currentPlant.getPlantSpecies()));


    return listItem;
  }
  public String cutUnusedData(String string) {
    string = string.substring(16, string.length()-2);
    return string;
  }

  public String cutLast(String str) {
    if (str != null && str.length() > 0 && str.charAt(str.length() - 1) == 'x') {
      str = str.substring(0, str.length() - 1);
    }
    return str;
  }

}
